characters = {
    "b-2": (
        14,
        "data:image/png;base64,",
    ),
    "O39": (
        19,
        "data:image/png;base64,",
    ),
    "t": (
        14,
        "data:image/png;base64,",
    ),
}


def glyph_to_image(glyph: str) -> str:
    width, data_uri = characters[glyph]
    return f'<img src="{data_uri}" alt="{glyph}" width="{width}" height="38"/>'
